public class breakstatment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// want to print 1 to 100 numbers but skip 40 to 60 ;
		for (int i = 1; i <= 10; i++) {
			if (i == 5) {
				break;
			}

			System.out.println(i);
			
		}
		
	}

	}


