package hellopackage;

public class unaryoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int a =20 ,b=40;
		 System.out.println(a++);
		 System.out.println(a++);
		 System.out.println(b--);
		 System.out.println(b--);
		 System.out.println(--b);
		  
	}

}
